CREATE TABLE IF NOT EXISTS `data_dict` (
    `field_number` varchar(60) NOT NULL default '',
    `field_label` varchar(100) default NULL,
    `field_type` varchar(60) default NULL,
    `datatype` varchar(60) default NULL,
    `label_number` varchar(60) default NULL,
    `list_code` varchar(60) default NULL,
    `is_repeat` varchar(60) default NULL,
    `clar_note` varchar(60) default NULL,
    `field_name` varchar(100) default NULL,
    `link_table` varchar(60) default NULL,
    `link_field` varchar(60) default NULL,  
    `remove` varchar(60) default NULL,
    `chk_remove` varchar(60) default NULL,
    `num_levels` varchar(60) default NULL,
    `lite` varchar(60) default NULL,
    `bool_def` varchar(60) default NULL,
    `numb_def` varchar(60) default NULL,
    `entity` varchar(60) default NULL,
    `find` varchar(60) default NULL,
    `essential` varchar(1) default 'n',
    `enabled` varchar(1) NOT NULL default 'y',
    `visible_new` varchar(1) NOT NULL default 'y',
    `visible_edit` varchar(1) NOT NULL default 'y',
    `visible_view` varchar(1) NOT NULL default 'y',
    `visible_search` varchar(1) NOT NULL default 'y',
    `visible_search_display` varchar(1) NOT NULL default 'y',
    `visible_browse` varchar(1) NOT NULL default 'n',
    `visible_browse_editable` varchar(1) NOT NULL default 'y',
    `validation` varchar(100) default NULL,
    `required` varchar(1) default 'n',
    `visible_adv_search` varchar(1) NOT NULL default 'y',
    `visible_adv_search_display` varchar(1) NOT NULL default 'y',
    PRIMARY KEY  (`field_number`)
) ENGINE=InnoDB ;

